import React, { Component } from 'react';
import Buscador from './componentes/Buscador';
import Resultado from './componentes/Resultado'
//import './App.css';

class App extends Component {


  state = { 
    termino:'',
    imagenes : [],
    pagina:''
   }

   scroll = () => {
     const elemento = document.querySelector('.jumbotron');
     elemento.scrollIntoView('smooth','end');
   }

   paginaAnterior = () => {

    // leer el state de la pagina actual
    let pagina = this.state.pagina;

    //leer que si la pagina es 1, no debería ir para atras
    if (pagina === 1) {
      return null;
    }

    //sumar uno a la pagina actual
    pagina --;
    //agregar el cambio al state
    this.setState({
      pagina
    }, () => {
      this.consultarApi();
    });


   }

   paginaSiguiente = () => {

    // leer el state de la pagina actual
    let pagina = this.state.pagina;

    //sumar uno a la pagina actual
    pagina ++;
    //agregar el cambio al state
    this.setState({
      pagina
    }, () => {
      this.consultarApi();
    });

   }


   consultarApi = () => {
    const pagina = this.state.pagina;     
    const url = `https://pixabay.com/api/?key=17714024-c2111c4db12767d500f6a7375&q=${this.state.termino}&per_page=30&page=${pagina}`;
    console.log(url);

     fetch(url)
       .then(respuesta=>respuesta.json())
       .then(resultado => this.setState({ imagenes : resultado.hits }))


   }

   datosBusqueda = (termino) => {
      this.setState({
          termino: termino,
          pagina:1
        }, ()=>{
            this.consultarApi();
           })
    }

  render(){
    return (

      <div className='app container'>
        <div className='jumbotron'>
          <p className='lead text-center'>Buscador de Imagenes</p>

          <Buscador datosBusqueda={this.datosBusqueda} />
        </div>
          <div className='row justify-content-center'>
            <Resultado 
            imagenes = {this.state.imagenes}
            paginaAnterior={this.paginaAnterior}
            paginaSiguiente={this.paginaSiguiente}
          />
          </div>
      </div>

    );
}
}


export default App;
